import { Component } from "react";
import getCookie from "../../../features/getCookie";

export default class AccountInfo extends Component {
    constructor(){
        super();
        this.state= {
            data: [],
        }

        this.logOut = this.logOut.bind(this);
    }

    async componentDidMount() {
        fetch('https://localhost:7013/api/user/GetInfo/' + getCookie("userId"))
        .then((Response) => Response.json())
        .then(async (Result) => {
            this.setState({ data: Result.responseData });
        });
    }
    logOut(){
        document.cookie = "userId=" + null;
        document.cookie = "isLogin=false";
        window.location.reload();
    }

    render() {
        return(
            <div>
                <p className="accountAvatar" >{ getCookie("username")[0].toUpperCase() }</p>
                <label htmlFor="username">Имя: </label>
                <input type="text" name="username" id="username" value={this.state.data.login} />
                <br />
                <label htmlFor="password">Пароль: </label>
                <input type="text" name="password" id="password" value={this.state.data.password} />
                <button onClick={this.logOut}>Выйти</button>
            </div>
        );
    }
}
